INSERT INTO help (parentID, hash, topic, content, created, updated) VALUES (-1, md5('media_write'), 'Media', 'From the Media module, users may click “Edit” from the “Edit Metadata” column for any file. This will redirect to a page allowing users to update information about the file including Date of Administration, Comments, and file visibility (hidden or visible). To finalize an update, users must click “Update File”. Note that fields such as PSCID, Visit Label, Site, and Instrument cannot be modified on uploaded media files.', '2017-08-24 00:00:00', NULL);
